import React, { useState } from 'react';
import { actualizarRenta } from '../../../../../DataBase/DataOperations';

function RentUpdate() {
    const [idRenta, setIdRenta] = useState('');
    const [nuevoEstatus, setNuevoEstatus] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const [successMessage, setSuccessMessage] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const resultado = await actualizarRenta(idRenta, null, null, null, null, null, null, nuevoEstatus);
            if (resultado === 0) {
                setSuccessMessage('Estatus de renta actualizado correctamente.');
                setIdRenta('');
                setNuevoEstatus('');
            } else {
                setErrorMessage('Hubo un error al actualizar el estatus de la renta.');
            }
        } catch (error) {
            console.error('Error al actualizar estatus de renta:', error);
            setErrorMessage('Hubo un error al actualizar el estatus de la renta.');
        }
    };

    return (
        <div>
            <h2>Actualizar Estatus de Renta</h2>
            {errorMessage && <div>{errorMessage}</div>}
            {successMessage && <div>{successMessage}</div>}
            <form onSubmit={handleSubmit}>
                <label>
                    ID de la Renta:
                    <input type="text" value={idRenta} onChange={(e) => setIdRenta(e.target.value)} required />
                </label>
                <label>
                    Nuevo Estatus:
                    <select value={nuevoEstatus} onChange={(e) => setNuevoEstatus(e.target.value)} required>
                        <option value="">Seleccionar...</option>
                        <option value="0">En Progreso</option>
                        <option value="1">Completada</option>
                    </select>
                </label>
                <button type="submit">Actualizar Estatus</button>
            </form>
        </div>
    );
}

export default RentUpdate;
