import React, { useState } from 'react';
import { crearRenta } from '../../../../../DataBase/DataOperations';

function CreateRent() {
    const [idUsuario, setIdUsuario] = useState('');
    const [idPelicula, setIdPelicula] = useState('');
    const [fechaRenta, setFechaRenta] = useState('');
    const [diasDeRenta, setDiasDeRenta] = useState(5); // Valor por defecto: 5 días
    const [estatus, setEstatus] = useState(0); // Valor por defecto: 0
    const [errorMessage, setErrorMessage] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (diasDeRenta <= 0) {
            setErrorMessage('El número de días de renta debe ser mayor que 0.');
            return;
        }
        try {
            const result = await crearRenta(idUsuario, idPelicula, fechaRenta, diasDeRenta, estatus);
            if (result === 1) {
                alert('Renta creada correctamente.');
                // Limpiar los campos después de una creación exitosa
                setIdUsuario('');
                setIdPelicula('');
                setFechaRenta('');
                setDiasDeRenta(5);
                setEstatus(0);
                setErrorMessage('');
            } else {
                if (result===-1){
                alert('Error al crear la renta por el ID usuario.');
                }else{
                    alert('Error al crear la renta por el ID película.');
                }
            }
        } catch (error) {
            console.error('Error al crear la renta:', error);
            alert('Error al crear la renta.');
        }
    };

    return (
        <div>
            <h2>Crear Renta</h2>
            {errorMessage && <div>{errorMessage}</div>}
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="idUsuario">ID Usuario:</label>
                    <input type="text" id="idUsuario" value={idUsuario} onChange={(e) => setIdUsuario(e.target.value)} required />
                </div>
                <div>
                    <label htmlFor="idPelicula">ID Película:</label>
                    <input type="text" id="idPelicula" value={idPelicula} onChange={(e) => setIdPelicula(e.target.value)} required />
                </div>
                <div>
                    <label htmlFor="fechaRenta">Fecha de Renta:</label>
                    <input type="date" id="fechaRenta" value={fechaRenta} onChange={(e) => setFechaRenta(e.target.value)} required />
                </div>
                <div>
                    <label htmlFor="diasDeRenta">Días de Renta:</label>
                    <input type="number" id="diasDeRenta" value={diasDeRenta} onChange={(e) => setDiasDeRenta(e.target.value)} />
                </div>
                <div>
                    <label htmlFor="estatus">Estatus:</label>
                    <select id="estatus" value={estatus} onChange={(e) => setEstatus(e.target.value)}>
                        <option value="0">0</option>
                        <option value="1">1</option>
                    </select>
                </div>
                <button type="submit">Crear Renta</button>
            </form>
        </div>
    );
}

export default CreateRent;
