import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { leerUsuarioPorId } from '../../../../../DataBase/DataOperations.js';

function UserList() {
  const [idUsuarioBuscar, setIdUsuario] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!idUsuarioBuscar) return;

    try {
      const usuario = await leerUsuarioPorId(idUsuarioBuscar);
      if (usuario) {
        navigate(`/users/show/${idUsuarioBuscar}`);
      } else {
        alert('No se encontró ningún usuario con ese ID');
      }
    } catch (error) {
      console.error('Error al buscar usuario por ID:', error);
    }
  };

  return (
    <div>
      <h2>Buscar Usuario por ID</h2>
      <form onSubmit={handleSubmit}>
        <label>
          ID del Usuario:
          <input type="text" value={idUsuarioBuscar} onChange={(e) => setIdUsuario(e.target.value)} />
        </label>
        <button type="submit">Buscar</button>
      </form>
    </div>
  );
}

export default UserList;
