import React, { useEffect, useState } from 'react';
import { leerUsuarioPorId, leerUsuarios } from '../../../../../DataBase/DataOperations.js'; // Importa las funciones necesarias desde DataOperations.js

function ReadUsers() {
  const [usuarios, setUsuarios] = useState([]);
  const [idUsuarioBuscar, setIdUsuario] = useState('');
  const [usuarioEncontrado, setUsuarioEncontrado] = useState(null);

  useEffect(() => {
    cargarUsuarios();
  }, []);

  const cargarUsuarios = async () => {
    try {
      const usuariosData = await leerUsuarios();
      setUsuarios(usuariosData);
    } catch (error) {
      console.error('Error al cargar usuarios:', error);
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!idUsuarioBuscar) return;

    try {
      const usuario = await leerUsuarioPorId(idUsuarioBuscar);
      if (usuario === 1) {
        window.location.reload();
      } else if (usuario !== 0) {
        setUsuarioEncontrado(usuario);
        alert(`ID: ${usuario.idUsuario}\nNombre: ${usuario.nombre}\nApellido Paterno: ${usuario.apPat}\nApellido Materno: ${usuario.apMat}\nEmail: ${usuario.email}`);
      } else {
        alert('No se encontró ningún usuario con ese ID');
      }
    } catch (error) {
      console.error('Error al buscar usuario por ID:', error);
    }
  };


  return (
    <div>
      <h2>Buscar Usuario por ID</h2>
      <form onSubmit={handleSubmit}>
        <label>
          ID del Usuario:
          <input type="text" value={idUsuarioBuscar} onChange={(e) => setIdUsuario(e.target.value)} />
        </label>
        <button type="submit">Buscar</button>
      </form>
      <h2>Usuarios</h2>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nombre</th>
            <th>Apellido Paterno</th>
            <th>Apellido Materno</th>
            <th>Email</th>
          </tr>
        </thead>
        <tbody>
          {usuarioEncontrado ? (
            <tr key={usuarioEncontrado.idUsuario}>
              <td>{usuarioEncontrado.idUsuario}</td>
              <td>{usuarioEncontrado.nombre}</td>
              <td>{usuarioEncontrado.apPat}</td>
              <td>{usuarioEncontrado.apMat}</td>
              <td>{usuarioEncontrado.email}</td>
            </tr>
          ) : (
            usuarios.map((usuario) => (
              <tr key={usuario.idUsuario}>
                <td>{usuario.idUsuario}</td>
                <td>{usuario.nombre}</td>
                <td>{usuario.apPat}</td>
                <td>{usuario.apMat}</td>
                <td>{usuario.email}</td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
}

export default ReadUsers;
