import React, { useState } from 'react';
import { leerUsuarioPorId } from '../../../../../DataBase/DataOperations.js';

function UserDetails() {
  const [idUsuario, setIdUsuario] = useState('');
  const [usuario, setUsuario] = useState(null);

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!idUsuario) return;

    try {
      const user = await leerUsuarioPorId(idUsuario);
      if (user !== 0) {
        setUsuario(user);
      } else {
        alert('No se encontró ningún usuario con ese ID');
      }
    } catch (error) {
      console.error('Error al buscar usuario por ID:', error);
    }
  };

  return (
    <div>
      <h2>Detalles del Usuario</h2>
      <form onSubmit={handleSubmit}>
        <label>
          ID del Usuario:
          <input type="text" value={idUsuario} onChange={(e) => setIdUsuario(e.target.value)} />
        </label>
        <button type="submit">Buscar</button>
      </form>

      {usuario && (
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Nombre</th>
              <th>Apellido Paterno</th>
              <th>Apellido Materno</th>
              <th>Email</th>
            </tr>
          </thead>
          <tbody>
            <tr key={usuario.idUsuario}>
              <td>{usuario.idUsuario}</td>
              <td>{usuario.nombre}</td>
              <td>{usuario.apPat}</td>
              <td>{usuario.apMat}</td>
              <td>{usuario.email}</td>
            </tr>
          </tbody>
        </table>
      )}
    </div>
  );
}

export default UserDetails;
