import './Navigation.css'

export default function Navigation(){
    const [mostrarOpcionesUsuarios, setMostrarOpcionesUsuarios] = useState(false);
  const [mostrarOpcionesPeliculas, setMostrarOpcionesPeliculas] = useState(false);
  const [mostrarOpcionesRentas, setMostrarOpcionesRentas] = useState(false);

  return (
    <div className="menu-principal">
      <h1 className="titulo">ClonBuster</h1>
      <div className="banner">ClonBuster</div>

      <div className="opcion" onMouseEnter={() => setMostrarOpcionesUsuarios(true)} onMouseLeave={() => setMostrarOpcionesUsuarios(false)}>
        Usuarios
        {mostrarOpcionesUsuarios && (
          <div className="opciones">
            <div>Create</div>
            <div>Read</div>
            <div>Update</div>
            <div>Delete</div>
          </div>
        )}
      </div>

      <div className="opcion" onMouseEnter={() => setMostrarOpcionesPeliculas(true)} onMouseLeave={() => setMostrarOpcionesPeliculas(false)}>
        Pel√≠culas
        {mostrarOpcionesPeliculas && (
          <div className="opciones">
            <div>Create</div>
            <div>Read</div>
            <div>Update</div>
            <div>Delete</div>
          </div>
        )}
      </div>

      <div className="opcion" onMouseEnter={() => setMostrarOpcionesRentas(true)} onMouseLeave={() => setMostrarOpcionesRentas(false)}>
        Rentas
        {mostrarOpcionesRentas && (
          <div className="opciones">
            <div>Create</div>
            <div>Read</div>
            <div>Update</div>
            <div>Delete</div>
          </div>
        )}
      </div>
    </div>
  );
}
