import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { leerUsuarios, leerUsuarioPorId } from '../../../../../DataBase/DataOperations.js';

function ReadUsers() {
  const [usuarios, setUsuarios] = useState([]);
  const [idUsuarioBuscar, setIdUsuarioBuscar] = useState('');
  const [usuarioEncontrado, setUsuarioEncontrado] = useState(null);

  useEffect(() => {
    cargarUsuarios();
  }, []);

  const cargarUsuarios = async () => {
    try {
      const usuariosData = await leerUsuarios();
      setUsuarios(usuariosData);
    } catch (error) {
      console.error('Error al cargar usuarios:', error);
    }
  };

  const handleBuscarUsuario = async () => {
    try {
      const usuario = await leerUsuarioPorId(idUsuarioBuscar);
      if (usuario) {
        setUsuarioEncontrado(usuario);
        mostrarDetalles(usuario);
      } else {
        alert('No se encontró ningún usuario con ese ID.');
      }
    } catch (error) {
      console.error('Error al buscar usuario por ID:', error);
    }
  };

  const mostrarDetalles = (usuario) => {
    alert(`Detalles del usuario:
      ID: ${usuario.idUsuario}
      Nombre: ${usuario.nombre}
      Apellido Paterno: ${usuario.apPat}
      Apellido Materno: ${usuario.apMat}
      Email: ${usuario.email}
    `);
  };

  return (
    <div>
      <h2>Usuarios</h2>
      <div>
        <label>
          Buscar usuario por ID:
          <input type="text" value={idUsuarioBuscar} onChange={(e) => setIdUsuarioBuscar(e.target.value)} />
        </label>
        <button onClick={handleBuscarUsuario}>Buscar</button>
      </div>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nombre</th>
            <th>Apellido Paterno</th>
            <th>Apellido Materno</th>
            <th>Email</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {usuarios.map(usuario => (
            <tr key={usuario.idUsuario}>
              <td>{usuario.idUsuario}</td>
              <td>{usuario.nombre}</td>
              <td>{usuario.apPat}</td>
              <td>{usuario.apMat}</td>
              <td>{usuario.email}</td>
              <td>
                <Link to={`/showUser/${usuario.idUsuario}`}>Ver detalles</Link>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ReadUsers;
