import React, { useState, useEffect } from 'react';
import { leerRentasPorUsuario, leerRentasPorPelicula } from '../../../../../DataBase/DataOperations';

function ReadRents() {
    const [idUsuario, setIdUsuario] = useState('');
    const [idPelicula, setIdPelicula] = useState('');
    const [rentas, setRentas] = useState([]);
    const [errorMessage, setErrorMessage] = useState('');

    useEffect(() => {
        cargarRentas();
    }, [idUsuario, idPelicula]);

    const cargarRentas = async () => {
        try {
            let rentasData = [];
            if (idUsuario) {
                rentasData = await leerRentasPorUsuario(parseInt(idUsuario));
            } else if (idPelicula) {
                rentasData = await leerRentasPorPelicula(parseInt(idPelicula));
            }
            setRentas(rentasData);
        } catch (error) {
            console.error('Error al cargar rentas:', error);
            setErrorMessage('Hubo un error al cargar las rentas.');
        }
    };

    const formatDate = (dateString) => {
        const options = { year: 'numeric', month: 'long', day: 'numeric' };
        return new Date(dateString).toLocaleDateString('es-ES', options);
    };

    return (
        <div>
            <h2>Ver Rentas</h2>
            {errorMessage && <div>{errorMessage}</div>}
            <div>
                <label>
                    Filtrar por ID de Usuario:
                    <input type="text" value={idUsuario} onChange={(e) => setIdUsuario(e.target.value)} />
                </label>
                <label>
                    Filtrar por ID de Película:
                    <input type="text" value={idPelicula} onChange={(e) => setIdPelicula(e.target.value)} />
                </label>
            </div>
            <table>
                <thead>
                    <tr>
                        <th>ID Usuario</th>
                        <th>ID Película</th>
                        <th>Fecha de Renta</th>
                        <th>Días de Renta</th>
                        <th>Estatus</th>
                    </tr>
                </thead>
                <tbody>
                    {rentas.map(renta => (
                        <tr key={renta.idRent}>
                            <td>{renta.idUsuario}</td>
                            <td>{renta.idPelicula}</td>
                            <td>{formatDate(renta.fecha_renta)}</td>
                            <td>{renta.dias_de_renta}</td>
                            <td>{renta.estatus}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default ReadRents;
