import React from 'react';
import { useParams } from 'react-router-dom';
import { leerUsuarioPorId } from '../../../../../DataBase/DataOperations.js';

function UserDetails() {
  const usuarios = leerUsuarioPorId()
  const params = useParams();
  const userId = parseInt(params.idUsuario)
  const usuario = leerUsuarioPorId(userId);



  return (
    <div>
      <h2>Detalles del usuario</h2>
      <div>
      <strong>Nombre:</strong> {usuario.nombre}<br />
        <strong>ID:</strong> {usuario.idUsuario}<br />

        <strong>Apellido Paterno:</strong> {usuario.apPat}<br />
        <strong>Apellido Materno:</strong> {usuario.apMat}<br />
        <strong>Email:</strong> {usuario.email}<br />
        {/* Agrega m√°s detalles si es necesario */}
      </div>
    </div>
  );
}

export default UserDetails;
