import React, { useState } from 'react';
import { leerRentaPorId, actualizarRenta } from '../../../../../DataBase/DataOperations';

function ReadRents() {
    const [searchId, setSearchId] = useState('');
    const [rentaEncontrada, setRentaEncontrada] = useState(null);
    const [errorMessage, setErrorMessage] = useState('');

    const buscarRentaPorId = async () => {
        try {
            const renta = await leerRentaPorId(parseInt(searchId));
            if (renta) {
                setRentaEncontrada(renta);
                setErrorMessage('');
            } else {
                setErrorMessage('No se encontró ninguna renta con ese ID.');
                setRentaEncontrada(null);
            }
        } catch (error) {
            console.error('Error al buscar renta por ID:', error);
            setErrorMessage('Hubo un error al buscar la renta.');
            setRentaEncontrada(null);
        }
    };

    const actualizarEstatusRenta = async (estatus) => {
        try {
            if (rentaEncontrada) {
                const resultado = await actualizarRenta(rentaEncontrada.idRent, estatus);
                if (resultado === 0) {
                    setRentaEncontrada({ ...rentaEncontrada, estatus });
                    alert('Estatus de renta actualizado correctamente.');
                } else {
                    alert('Error al actualizar el estatus de la renta.');
                }
            }
        } catch (error) {
            console.error('Error al actualizar estatus de renta:', error);
            alert('Hubo un error al actualizar el estatus de la renta.');
        }
    };

    const formatDate = (dateString) => {
        const options = { year: 'numeric', month: 'long', day: 'numeric' };
        return new Date(dateString).toLocaleDateString('es-ES', options);
    };

    return (
        <div>
            <h2>Ver Rentas</h2>
            <div>
                <label>
                    Buscar renta por ID:
                    <input type="text" value={searchId} onChange={(e) => setSearchId(e.target.value)} />
                </label>
                <button onClick={buscarRentaPorId}>Buscar</button>
            </div>
            {errorMessage && <div>{errorMessage}</div>}
            {rentaEncontrada && (
                <div>
                    <h3>Detalles de la Renta</h3>
                    <p><strong>ID de Renta:</strong> {rentaEncontrada.idRent}</p>
                    <p><strong>ID de Usuario:</strong> {rentaEncontrada.idUsuario}</p>
                    <p><strong>ID de Película:</strong> {rentaEncontrada.idPelicula}</p>
                    <p><strong>Fecha de Renta:</strong> {formatDate(rentaEncontrada.fecha_renta)}</p>
                    <p><strong>Días de Renta:</strong> {rentaEncontrada.dias_de_renta}</p>
                    <p><strong>Estatus:</strong> {rentaEncontrada.estatus}</p>
                    <div>
                        <button onClick={() => actualizarEstatusRenta(1)}>Marcar como devuelta</button>
                    </div>
                </div>
            )}
        </div>
    );
}

export default ReadRents;
