import React, { useState } from 'react';
import { leerUsuarioPorId } from '../../../../../DataBase/DataOperations.js';

function UserDetails() {
  const [idUsuario, setIdUsuario] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!idUsuario) return;

    try {
      const usuario = await leerUsuarioPorId(idUsuario);
      if (usuario) {
        alert(`ID: ${usuario.idUsuario}\nNombre: ${usuario.nombre}\nApellido Paterno: ${usuario.apPat}\nApellido Materno: ${usuario.apMat}\nEmail: ${usuario.email}`);
      } else {
        alert('No se encontró ningún usuario con ese ID');
      }
    } catch (error) {
      console.error('Error al buscar usuario por ID:', error);
      alert('Ocurrió un error al buscar el usuario');
    }
  };

  return (
    <div>
      <h2>Detalles del Usuario</h2>
      <form onSubmit={handleSubmit}>
        <label>
          ID del Usuario:
          <input type="text" value={idUsuario} onChange={(e) => setIdUsuario(e.target.value)} />
        </label>
        <button type="submit">Buscar</button>
      </form>
    </div>
  );
}

export default UserDetails;
