import React, { useState } from 'react';
import { actualizarUsuario, leerUsuarioPorId } from '../../../../../DataBase/DataOperations.js';

function UpdateUser() {
  const [idUsuario, setIdUsuario] = useState('');
  const [nombre, setNombre] = useState('');
  const [apPat, setApPat] = useState('');
  const [apMat, setApMat] = useState('');
  const [email, setEmail] = useState('');
  const [mensaje, setMensaje] = useState('');

  const handleBuscarUsuario = async () => {
    try {
      const usuario = await leerUsuarioPorId(parseInt(idUsuario));
      if (usuario) {
        setNombre(usuario.nombre);
        setApPat(usuario.apPat);
        setApMat(usuario.apMat);
        setEmail(usuario.email);
      } else {
        alert('No se encontró ningún usuario con ese ID.');
      }
    } catch (error) {
      console.error('Error al buscar usuario por ID:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const resultado = await actualizarUsuario(parseInt(idUsuario), nombre, apPat, apMat, email);
      if (resultado === 0) {
        setMensaje('¡Usuario actualizado correctamente!');
      } else {
        setMensaje('¡Hubo un fallo al actualizar el usuario!');
      }
    } catch (error) {
      console.error('Error al actualizar usuario:', error);
      setMensaje('¡Hubo un fallo al actualizar el usuario!');
    }
  };

  return (
    <div>
      <h2>Actualizar Usuario</h2>
      <form onSubmit={handleSubmit}>
        <label>
          ID del Usuario (requerido):
          <input type="text" value={idUsuario} onChange={(e) => setIdUsuario(e.target.value)} required />
        </label>
        <button type="button" onClick={handleBuscarUsuario}>Buscar Usuario</button>
        <br />
        <label>
          Nombre:
          <input type="text" value={nombre} onChange={(e) => setNombre(e.target.value)} />
        </label>
        <br />
        <label>
          Apellido Paterno:
          <input type="text" value={apPat} onChange={(e) => setApPat(e.target.value)} />
        </label>
        <br />
        <label>
          Apellido Materno:
          <input type="text" value={apMat} onChange={(e) => setApMat(e.target.value)} />
        </label>
        <br />
        <label>
          Email:
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
        </label>
        <br />
        <button type="submit">Actualizar Usuario</button>
      </form>
      {mensaje && <div>{mensaje}</div>}
    </div>
  );
}

export default UpdateUser;
